INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (37,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','less','x86_64','200427','436','10.el6','2013-11-27 23:49:31','A text file browser similar to more, but better','The less utility is a text file browser that resembles more, but has\nmore capabilities.  Less allows you to move backwards in the file as\nwell as forwards.  Since less doesn\'t have to read the entire input file\nbefore it starts, less starts up more quickly than text editors (for\nexample, vi).\n\nYou should install less because it is a basic utility for viewing text\nfiles, and you\'ll use it frequently.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
