INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (39,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libtiff','x86_64','961971','3.9.4','9.el6_3','2013-11-27 23:49:11','Library of functions for manipulating TIFF format image files','The libtiff package contains a library of functions for manipulating\nTIFF (Tagged Image File Format) image format files.  TIFF is a widely\nused file format for bitmapped images.  TIFF files usually end in the\n.tif extension and they are often quite large.\n\nThe libtiff package should be installed if you need to manipulate TIFF\nformat image files.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (40,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','eclipse-emf','x86_64','5516430','2.6.0','1.el6','2013-11-27 23:51:55','Eclipse Modeling Framework (EMF) Eclipse plugin','The Eclipse Modeling Framework (EMF) allows developers to build tools and\nother applications based on a structured data model. From a model\nspecification described in XMI, EMF provides tools and runtime support to\nproduce a set of Java classes for the model, along with a set of adapter\nclasses that enable viewing and command-based editing of the model, and a\nbasic editor.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (41,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libgpg-error-devel','x86_64','26694','1.7','4.el6','2013-11-27 23:50:43','Development files for the libgpg-error package','This is a library that defines common error values for all GnuPG\ncomponents.  Among these are GPG, GPGSM, GPGME, GPG-Agent, libgcrypt,\npinentry, SmartCard Daemon and possibly more in the future. This package\ncontains files necessary to develop applications using libgpg-error.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (42,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','docbook-slides','noarch','1953264','3.4.0','9.el6','2013-11-27 23:55:02','DocBook Slides document type and stylesheets','DocBook Slides provides customization layers of the both the\nSimplified and the full DocBook XML DTD, as well as the DocBook XSL\nStylesheets. This package contains the XML document type definition\nand stylesheets for processing DocBook Slides XML. The slides doctype\nand stylesheets are for generating presentations, primarily in HTML.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
