INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (47,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','jakarta-commons-discovery','noarch','88339','0.4','5.4.el6','2013-11-27 23:50:50','Jakarta Commons Discovery','The Discovery component is about discovering, or finding, implementations for\npluggable interfaces.  Pluggable interfaces are specified with the intent that\nmultiple implementations are, or will be, available to provide the service\ndescribed by the interface.  Discovery provides facilities for finding and\ninstantiating classes, and for lifecycle management of singleton (factory)\nclasses.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (48,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','psacct','x86_64','140803','6.3.2','63.el6_3.3','2013-11-27 23:55:11','Utilities for monitoring process activities','The psacct package contains several utilities for monitoring process\nactivities, including ac, lastcomm, accton and sa. The ac command\ndisplays statistics about how long users have been logged on. The\nlastcomm command displays information about previous executed\ncommands. The accton command turns process accounting on or off. The\nsa command summarizes information about previously executed\ncommands.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (49,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','mysql-libs','x86_64','4242971','5.1.66','2.el6_3','2013-11-27 23:49:41','The shared libraries required for MySQL clients','The mysql-libs package provides the essential shared libraries for any\nMySQL client program or interface. You will need to install this package\nto use any other MySQL package or any clients that need to connect to a\nMySQL server.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (50,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','emacs-common','x86_64','60444354','23.1','21.el6_2.3','2013-11-27 23:53:10','Emacs common files','Emacs is a powerful, customizable, self-documenting, modeless text\neditor. Emacs contains special code editing features, a scripting\nlanguage (elisp), and the capability to read mail, news, and more\nwithout leaving the editor.\n\nThis package contains all the common files needed by emacs or emacs-nox.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
