INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (53,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ConsoleKit-x11','x86_64','43984','0.4.1','3.el6','2013-11-27 23:50:52','X11-requiring add-ons for ConsoleKit','ConsoleKit contains some tools that require Xlib to be installed,\nthose are in this separate package so server systems need not install\nX. Applications (such as xorg-x11-xinit) and login managers (such as\ngdm) that need to register their X sessions with ConsoleKit needs to\nhave a Requires: for this package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (54,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','hunspell-en','noarch','1652534','0.20090216','7.1.el6','2013-11-27 23:55:14','English hunspell dictionaries','English (US, UK, etc.) hunspell dictionaries\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (55,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','genisoimage','x86_64','1512545','1.1.9','12.el6','2013-11-27 23:49:45','Creates an image of an ISO9660 filesystem','The genisoimage program is used as a pre-mastering program; i.e., it\ngenerates the ISO9660 filesystem. Genisoimage takes a snapshot of\na given directory tree and generates a binary image of the tree\nwhich will correspond to an ISO9660 filesystem when written to\na block device. Genisoimage is used for writing CD-ROMs, and includes\nsupport for creating bootable El Torito CD-ROMs.\n\nInstall the genisoimage package if you need a program for writing\nCD-ROMs.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (56,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','cronie','x86_64','169834','1.4.4','7.el6','2013-11-27 23:53:12','Cron daemon for executing programs at set times','Cronie contains the standard UNIX daemon crond that runs specified programs at\nscheduled times and related tools. It is a fork of the original vixie-cron and\nhas security and configuration enhancements like the ability to use pam and\nSELinux.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
