INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (60,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','audit-libs-devel','x86_64','71622','2.2','2.el6','2013-11-27 23:55:15','Header files for libaudit','The audit-libs-devel package contains the header files needed for\ndeveloping applications that need to use the audit framework libraries.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (61,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','python-libs','x86_64','1739012','2.6.6','36.el6','2013-11-27 23:49:47','The libraries for python runtime','The python interpreter can be embedded into applications wanting to\nuse python as an embedded scripting language.  The python-libs package\nprovides the libraries needed for this.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (62,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','radvd','x86_64','163258','1.6','1.el6','2013-11-27 23:53:14','A Router Advertisement daemon','radvd is the router advertisement daemon for IPv6.  It listens to router\nsolicitations and sends router advertisements as described in \"Neighbor\nDiscovery for IP Version 6 (IPv6)\" (RFC 2461).  With these advertisements\nhosts can automatically configure their addresses and some other\nparameters.  They also can choose a default router based on these\nadvertisements.\n\nInstall radvd if you are setting up IPv6 network and/or Mobile IPv6\nservices.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (63,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','freetype-devel','x86_64','2360910','2.3.11','6.el6_2.9','2013-11-27 23:49:15','FreeType development libraries and header files','The freetype-devel package includes the static libraries and header files\nfor the FreeType font rendering engine.\n\nInstall freetype-devel if you want to develop programs which will use\nFreeType.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (64,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gsm','x86_64','51364','1.0.13','4.el6','2013-11-27 23:52:12','Shared libraries for GSM speech compressor','Contains runtime shared libraries for libgsm, an implementation of\nthe European GSM 06.10 provisional standard for full-rate speech\ntranscoding, prI-ETS 300 036, which uses RPE/LTP (residual pulse\nexcitation/long term prediction) coding at 13 kbit/s.\n\nGSM 06.10 compresses frames of 162 13-bit samples (8 kHz sampling\nrate, i.e. a frame rate of 50 Hz) into 260 bits; for compatibility\nwith typical UNIX applications, our implementation turns frames of 160\n16-bit linear samples into 33-byte frames (1650 Bytes/s).\nThe quality of the algorithm is good enough for reliable speaker\nrecognition; even music often survives transcoding in recognizable\nform (given the bandwidth limitations of 8 kHz sampling rate).\n\nThe interfaces offered are a front end modelled after compress(1), and\na library API.  Compression and decompression run faster than realtime\non most SPARCstations.  The implementation has been verified against the\nETSI standard test patterns.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (65,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','tcp_wrappers','x86_64','149750','7.6','57.el6','2013-11-27 23:50:53','A security tool which acts as a wrapper for TCP daemons','The tcp_wrappers package provides small daemon programs which can\nmonitor and filter incoming requests for systat, finger, FTP, telnet,\nrlogin, rsh, exec, tftp, talk and other network services.\n\nInstall the tcp_wrappers program if you need a security tool for\nfiltering incoming network services requests.\n\nThis version also supports IPv6.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (66,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','liberation-serif-fonts','noarch','595788','1.05.1.20090721','5.el6','2013-11-27 23:55:33','Serif fonts to replace commonly used Microsoft Times New Roman','\nThe Liberation Fonts are intended to be replacements for the three most\ncommonly used fonts on Microsoft systems: Times New Roman, Arial, and Courier\nNew.\n\nThis is Serif TrueType fonts that replaced commonly used Microsoft Times New \\\nRoman.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (67,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','mtdev','x86_64','21165','1.1.2','5.el6','2013-11-27 23:49:50','Multitouch Protocol Translation Library','mtdev is a stand-alone library which transforms all variants of kernel MT\nevents to the slotted type B protocol. The events put into mtdev may be from\nany MT device, specifically type A without contact tracking, type A with\ncontact tracking, or type B with contact tracking.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (68,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','dhclient','x86_64','625041','4.1.1','34.P1.el6.centos','2013-11-27 23:53:19','Provides the dhclient ISC DHCP client daemon and dhclient-script','DHCP (Dynamic Host Configuration Protocol) is a protocol which allows\nindividual devices on an IP network to get their own network\nconfiguration information (IP address, subnetmask, broadcast address,\netc.) from a DHCP server. The overall purpose of DHCP is to make it\neasier to administer a large network.\n\nTo use DHCP on your network, install a DHCP service (or relay agent),\nand on clients run a DHCP client daemon.  The dhclient package\nprovides the ISC DHCP client daemon.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (69,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','dbus-devel','x86_64','107444','1.2.24','7.el6_3','2013-11-27 23:49:16','Development files for D-BUS','This package contains libraries and header files needed for\ndeveloping software that uses D-BUS.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (70,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ustr','x86_64','273983','1.0.4','9.1.el6','2013-11-27 23:52:15','String library, very low memory overhead, simple to import',' Micro string library, very low overhead from plain strdup() (Ave. 44% for\n0-20B strings). Very easy to use in existing C code. At it\'s simplest you can\njust include a single header file into your .c and start using it.\n This package also distributes pre-built shared libraries.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (71,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','rsync','x86_64','698710','3.0.6','9.el6','2013-11-27 23:50:55','A program for synchronizing files over a network','Rsync uses a reliable algorithm to bring remote and host files into\nsync very quickly. Rsync is fast because it just sends the differences\nin the files over the network instead of sending the complete\nfiles. Rsync is often used as a very powerful mirroring process or\njust as a more capable replacement for the rcp command. A technical\nreport which describes the rsync algorithm is included in this\npackage.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (72,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','un-core-dotum-fonts','noarch','7750439','1.0.2','0.15.080608.el6','2013-11-27 23:56:07','한글 은글꼴 Core 모음 - 은돋음','\n은글꼴 시리즈는 HLaTex개발자이신 은광희님이 1998년에 개발한 폰트입니다.\n2003년에 박원규님이 FontForge를 이용하여 트루타입폰트로 변환했습니다.\n은글꼴은 가장 일반적인 글꼴들입니다.\n\nCore 모음:\n- 은바탕: serif\n- 은디나루: fantasy\n- 은돋음: sans-serif\n- 은그래픽: sans-serif style\n- 은궁서: cursive, brush-stroke\n- 은필기: script\n\n이 패키지에는 은돋음글꼴이 포함되어 있습니다.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (73,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','python-kerberos','x86_64','46887','1.1','6.2.el6','2013-11-27 23:49:52','A high-level wrapper for Kerberos (GSSAPI) operations','This Python package is a high-level wrapper for Kerberos (GSSAPI) operations.\nThe goal is to avoid having to build a module that wraps the entire\nKerberos.framework, and instead offer a limited set of functions that do what\nis needed for client/serverKerberos authentication based on\n<http://www.ietf.org/rfc/rfc4559.txt>.\n\nMuch of the C-code here is adapted from Apache\'s mod_auth_kerb-5.0rc7.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (74,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gvfs-fuse','x86_64','32728','1.4.3','15.el6','2013-11-27 23:53:28','FUSE support for gvfs','This package provides support for applications not using gio\nto access the gvfs filesystems.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (75,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libtevent','x86_64','49600','0.9.17','1.el6','2013-11-27 23:49:17','The tevent library','Tevent is an event system based on the talloc memory management library.\nTevent has support for many event types, including timers, signals, and\nthe classic file descriptor events.\nTevent also provide helpers to deal with asynchronous code providing the\ntevent_req (Tevent Request) functions.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (76,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libmusicbrainz3','x86_64','411013','3.0.2','7.el6','2013-11-27 23:52:17','Library for accessing MusicBrainz servers','The MusicBrainz client library allows applications to make metadata\nlookup to a MusicBrainz server, generate signatures from WAV data and\ncreate CD Index Disk ids from audio CD roms.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (77,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','tbb','x86_64','205972','2.2','3.20090809.el6','2013-11-27 23:51:09','The Threading Building Blocks library abstracts low-level threading details','Threading Building Blocks (TBB) is a C++ runtime library that\nabstracts the low-level threading details necessary for optimal\nmulti-core performance.  It uses common C++ templates and coding style\nto eliminate tedious threading implementation work.\n\nTBB requires fewer lines of code to achieve parallelism than other\nthreading models.  The applications you write are portable across\nplatforms.  Since the library is also inherently scalable, no code\nmaintenance is required as more processor cores become available.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
