INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (85,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','docbook-simple','noarch','180902','1.1','8.el6','2013-11-27 23:49:56','Simplified DocBook is a small subset of the DocBook XML DTD','Simplified DocBook is an attempt to provide a proper subset of DocBook\nthat is simultaneously smaller and still useful. Documents written in\nthe subset must be 100% legal DocBook documents. This is a subset for\nsingle documents (articles, white papers, etc.), so there\'s no need\nfor books or sets, just \'articles\'. Simplified DocBook documents are\nviewable in online browsers if styled with CSS. (it\'s XML not SGML).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
