INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (94,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','mdadm','x86_64','860588','3.2.5','4.el6','2013-11-27 23:52:37','The mdadm program controls Linux md devices (software RAID arrays)','The mdadm program is used to create, manage, and monitor Linux MD (software\nRAID) devices.  As such, it provides similar functionality to the raidtools\npackage.  However, mdadm is a single program, and it can perform\nalmost all functions without a configuration file, though a configuration\nfile can be used to help with some common tasks.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
