INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (140,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libselinux','x86_64','130088','2.0.94','5.3.el6','2013-11-27 23:49:11','SELinux library and simple utilities','Security-enhanced Linux is a feature of the Linux® kernel and a number\nof utilities with enhanced security functionality designed to add\nmandatory access controls to Linux.  The Security-enhanced Linux\nkernel contains new architectural components originally developed to\nimprove the security of the Flask operating system. These\narchitectural components provide general support for the enforcement\nof many kinds of mandatory access control policies, including those\nbased on the concepts of Type Enforcement®, Role-based Access\nControl, and Multi-level Security.\n\nlibselinux provides an API for SELinux applications to get and set\nprocess and file security contexts and to obtain security policy\ndecisions.  Required for any applications that use the SELinux API.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (141,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','sudo','x86_64','2461511','1.8.6p3','7.el6','2013-11-27 23:55:02','Allows restricted root access for specified users','Sudo (superuser do) allows a system administrator to give certain\nusers (or groups of users) the ability to run some (or all) commands\nas root while logging all commands and arguments. Sudo operates on a\nper-command basis.  It is not a replacement for the shell.  Features\ninclude: the ability to restrict what commands a user may run on a\nper-host basis, copious logging of each command (providing a clear\naudit trail of who did what), a configurable timeout of the sudo\ncommand, and the ability to use the same configuration file (sudoers)\non many different machines.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (142,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pulseaudio-libs','x86_64','2699924','0.9.21','14.el6_3','2013-11-27 23:49:36','Libraries for PulseAudio clients','This package contains the runtime libraries for any application that wishes\nto interface with a PulseAudio sound server.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (143,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gnutls-devel','x86_64','1129398','2.8.5','10.el6','2013-11-27 23:50:43','Development files for the gnutls package','GnuTLS is a project that aims to develop a library which provides a secure\nlayer, over a reliable transport layer. Currently the GnuTLS library implements\nthe proposed standards by the IETF\'s TLS working group.\nThis package contains files needed for developing applications with\nthe GnuTLS library.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
