INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (151,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','wireless-tools','x86_64','211891','29','5.1.1.el6','2013-11-27 23:53:10','Wireless ethernet configuration tools','This package contain the Wireless tools, used to manipulate\nthe Wireless Extensions. The Wireless Extension is an interface\nallowing you to set Wireless LAN specific parameters and get the\nspecific stats for wireless networking equipment.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (152,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libasyncns','x86_64','52778','0.8','1.1.el6','2013-11-27 23:49:13','Asynchronous Name Service Library','A small and lightweight library that implements easy to use asynchronous\nwrappers around the libc NSS functions getaddrinfo(), res_query() and related.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (153,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ledmon','x86_64','128507','0.74','1.el6','2013-11-27 23:55:14','Enclosure LED Utilities','The ledmon and ledctl are user space applications design to control LED\nassociated with each slot in an enclosure or a drive bay. There are two\ntypes of system: 2-LED system (Activity LED, Status LED) and 3-LED system\n(Activity LED, Locate LED, Fail LED). User must have root privileges to\nuse this application.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (154,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','logrotate','x86_64','84725','3.7.8','16.el6','2013-11-27 23:49:45','Rotates, compresses, removes and mails system log files','The logrotate utility is designed to simplify the administration of\nlog files on a system which generates a lot of log files.  Logrotate\nallows for the automatic rotation compression, removal and mailing of\nlog files.  Logrotate can be set to handle a log file daily, weekly,\nmonthly or when the log file gets to a certain size.  Normally,\nlogrotate runs as a daily cron job.\n\nInstall the logrotate package if you need a utility to deal with the\nlog files on your system.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (155,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','nc','x86_64','111583','1.84','22.el6','2013-11-27 23:50:52','Reads and writes data across network connections using TCP or UDP','The nc package contains Netcat (the program is actually nc), a simple\nutility for reading and writing data across network connections, using\nthe TCP or UDP protocols. Netcat is intended to be a reliable back-end\ntool which can be used directly or easily driven by other programs and\nscripts.  Netcat is also a feature-rich network debugging and\nexploration tool, since it can create many different connections and\nhas many built-in capabilities.\n\nYou may want to install the netcat package if you are administering a\nnetwork and you\'d like to use its debugging and network exploration\ncapabilities.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (156,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','min12xxw','x86_64','64484','0.0.9','5.1.el6','2013-11-27 23:52:09','Converts PBM stream to Minolta printer language','This is a filter to convert pbmraw data such as produced by ghostscript to\nthe printer language of Minolta 1[234]xx W printers. It is meant to be used\nby the PostScript Description files of the drivers from the foomatic package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (157,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','redhat-lsb-graphics','x86_64','0','4.0','7.el6.centos','2013-11-27 23:53:12','LSB graphics libraries support for CentOS','The Linux Standard Base (LSB) Graphics Specifications define components\nthat are required to be present on an LSB conforming system.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (158,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ibus-libs','x86_64','271936','1.3.4','6.el6','2013-11-27 23:49:14','IBus libraries','This package contains the libraries for IBus\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (159,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ltrace','x86_64','151765','0.5','23.45svn.el6','2013-11-27 23:55:15','Tracks runtime library calls from dynamically linked executables','Ltrace is a debugging program which runs a specified command until the\ncommand exits.  While the command is executing, ltrace intercepts and\nrecords both the dynamic library calls called by the executed process\nand the signals received by the executed process.  Ltrace can also\nintercept and print system calls executed by the process.\n\nYou should install ltrace if you need a sysadmin tool for tracking the\nexecution of processes.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (160,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','SDL','x86_64','479624','1.2.14','3.el6','2013-11-27 23:49:46','A cross-platform multimedia library','Simple DirectMedia Layer (SDL) is a cross-platform multimedia library\ndesigned to provide fast access to the graphics frame buffer and audio\ndevice.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (161,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','m17n-db-telugu','noarch','6593','1.5.5','1.1.el6','2013-11-27 23:50:53','m17n-db input maps for Telugu','This package contains m17n-db input maps for Telugu.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (162,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gimp','x86_64','48113127','2.6.9','4.el6_3.3','2013-11-27 23:52:12','GNU Image Manipulation Program','GIMP (GNU Image Manipulation Program) is a powerful image composition and\nediting program, which can be extremely useful for creating logos and other\ngraphics for webpages. GIMP has many of the tools and filters you would expect\nto find in similar commercial offerings, and some interesting extras as well.\nGIMP provides a large image manipulation toolbox, including channel operations\nand layers, effects, sub-pixel imaging and anti-aliasing, and conversions, all\nwith multi-level undo.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (163,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libvirt','x86_64','5844876','0.10.2','18.el6','2013-11-27 23:53:15','Library providing a simple virtualization API','Libvirt is a C toolkit to interact with the virtualization capabilities\nof recent versions of Linux (and other OSes). The main package includes\nthe libvirtd server exporting the virtualization support.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (164,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','apr','x86_64','303205','1.3.9','5.el6_2','2013-11-27 23:49:16','Apache Portable Runtime library','The mission of the Apache Portable Runtime (APR) is to provide a\nfree library of C data structures and routines, forming a system\nportability layer to as many operating systems as possible,\nincluding Unices, MS Win32, BeOS and OS/2.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (165,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','lohit-bengali-fonts','noarch','170948','2.4.3','6.el6','2013-11-27 23:55:27','Free Bengali font','This package provides a free Bengali truetype/opentype font.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
