INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (229,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','popt','x86_64','83420','1.13','7.el6','2013-11-27 23:49:09','C library for parsing command line parameters','Popt is a C library for parsing command line parameters. Popt was\nheavily influenced by the getopt() and getopt_long() functions, but\nit improves on them by allowing more powerful argument expansion.\nPopt can parse arbitrary argv[] style arrays and automatically set\nvariables based on command line arguments. Popt allows command line\narguments to be aliased via configuration files and includes utility\nfunctions for parsing arbitrary strings into argv[] arrays using\nshell-like rules.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (230,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','bsf','noarch','123410','2.4.0','4.1.el6','2013-11-27 23:51:49','Bean Scripting Framework','Bean Scripting Framework (BSF) is a set of Java classes which provides\nscripting language support within Java applications, and access to Java\nobjects and methods from scripting languages. BSF allows one to write\nJSPs in languages other than Java while providing access to the Java\nclass library. In addition, BSF permits any Java application to be\nimplemented in part (or dynamically extended) by a language that is\nembedded within it. This is achieved by providing an API that permits\ncalling scripting language engines from within Java, as well as an\nobject registry that exposes Java objects to these scripting language\nengines.\n\nBSF supports several scripting languages currently:\n* Javascript (using Rhino ECMAScript, from the Mozilla project)\n* Python (using either Jython or JPython)\n* Tcl (using Jacl)\n* NetRexx (an extension of the IBM REXX scripting language in Java)\n* XSLT Stylesheets (as a component of Apache XML project\'s Xalan and\nXerces)\n\nIn addition, the following languages are supported with their own BSF\nengines:\n* Java (using BeanShell, from the BeanShell project)\n* JRuby\n* JudoScript\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (231,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pygtk2-devel','x86_64','1155047','2.16.0','3.el6','2013-11-27 23:54:54','Development files for building add-on libraries','This package contains files required to build wrappers for GTK+ add-on\nlibraries so that they interoperate with pygtk.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (232,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-libwww-perl','noarch','896924','5.833','2.el6','2013-11-27 23:50:39','A Perl interface to the World-Wide Web','The libwww-perl collection is a set of Perl modules which provides a\nsimple and consistent application programming interface to the\nWorld-Wide Web.  The main focus of the library is to provide classes\nand functions that allow you to write WWW clients. The library also\ncontain modules that are of more general use and even classes that\nhelp you implement simple HTTP servers.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (233,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ilmbase','x86_64','443586','1.0.1','6.1.el6','2013-11-27 23:49:30','Abstraction/convenience libraries','Half is a class that encapsulates the ilm 16-bit floating-point format.\n\nIlmThread is a thread abstraction library for use with OpenEXR\nand other software packages.\n\nImath implements 2D and 3D vectors, 3x3 and 4x4 matrices, quaternions\nand other useful 2D and 3D math functions.\n\nIex is an exception-handling library.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
