INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (256,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pth','x86_64','261931','2.0.7','9.3.el6','2013-11-27 23:49:45','The GNU Portable Threads library','Pth is a very portable POSIX/ANSI-C based library for Unix platforms\nwhich provides non-preemptive priority-based scheduling for multiple\nthreads of execution (\"multithreading\") inside server applications.\nAll threads run in the same address space of the server application,\nbut each thread has it\'s own individual program-counter, run-time\nstack, signal mask and errno variable.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (257,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','bzip2','x86_64','79087','1.0.5','7.el6_0','2013-11-27 23:49:14','A file compression utility','Bzip2 is a freely available, patent-free, high quality data compressor.\nBzip2 compresses files to within 10 to 15 percent of the capabilities\nof the best techniques available.  However, bzip2 has the added benefit\nof being approximately two times faster at compression and six times\nfaster at decompression than those techniques.  Bzip2 is not the\nfastest compression utility, but it does strike a balance between speed\nand compression capability.\n\nInstall bzip2 if you need a compression utility.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
