INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (261,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libxml2-python','x86_64','1976700','2.7.6','8.el6_3.4','2013-11-27 23:49:50','Python bindings for the libxml2 library','The libxml2-python package contains a module that permits applications\nwritten in the Python programming language to use the interface\nsupplied by the libxml2 library to manipulate XML files.\n\nThis library allows to manipulate XML files. It includes support\nto read, modify and write XML and HTML files. There is DTDs support\nthis includes parsing and validation even with complex DTDs, either\nat parse time or later once the document has been modified.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (262,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','postgresql','x86_64','15168614','8.4.13','1.el6_3','2013-11-27 23:53:17','PostgreSQL client programs','PostgreSQL is an advanced Object-Relational database management system\n(DBMS) that supports almost all SQL constructs (including\ntransactions, subselects and user-defined types and functions). The\npostgresql package includes the client programs and libraries that\nyou\'ll need to access a PostgreSQL DBMS server.  These PostgreSQL\nclient programs are programs that directly manipulate the internal\nstructure of PostgreSQL databases on a PostgreSQL server. These client\nprograms can be located on the same machine with the PostgreSQL\nserver, or may be on a remote machine which accesses a PostgreSQL\nserver over a network connection. This package contains the docs\nin HTML for the whole package, as well as command-line utilities for\nmanaging PostgreSQL databases on a PostgreSQL server.\n\nIf you want to manipulate a PostgreSQL database on a local or remote PostgreSQL\nserver, you need this package. You also need to install this package\nif you\'re installing the postgresql-server package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (263,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','btparser','x86_64','135358','0.17','1.el6','2013-11-27 23:49:16','Parser and analyzer for backtraces produced by GDB','Btparser is a backtrace parser and analyzer, which works with\nbacktraces produced by the GNU Project Debugger. It can parse a text\nfile with a backtrace to a tree of C structures, allowing to analyze\nthe threads and frames of the backtrace and work with them.\n\nBtparser also contains some backtrace manipulation and extraction\nroutines:\n- it can find a frame in the crash-time backtrace where the program\n  most likely crashed (a chance is that the function described in that\n  frame is buggy)\n- it can produce a duplication hash of the backtrace, which helps to\n  discover that two crash-time backtraces are duplicates, triggered by\n  the same flaw of the code\n- it can \"rate\" the backtrace quality, which depends on the number of\n  frames with and without the function name known (missing function\n  name is caused by missing debugging symbols)\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (264,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libdv','x86_64','182449','1.0.0','8.1.el6','2013-11-27 23:52:15','Software decoder for DV format video','The Quasar DV codec (libdv) is a software codec for DV video, the\nencoding format used by most digital camcorders, typically those that\nsupport the IEEE 1394 (a.k.a. FireWire or i.Link) interface. libdv was\ndeveloped according to the official standards for DV video: IEC 61834\nand SMPTE 314M.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
