INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (267,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','blktrace','x86_64','1041979','1.0.1','6.el6','2013-11-27 23:49:51','Utilities for performing block layer IO tracing in the linux kernel','blktrace is a block layer IO tracing mechanism which provides detailed\ninformation about request queue operations to user space.  This package\nincludes both blktrace, a utility which gathers event traces from the kernel;\nand blkparse, a utility which formats trace data collected by blktrace.\n\nYou should install the blktrace package if you need to gather detailed\ninformation about IO patterns.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (268,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pulseaudio','x86_64','1990955','0.9.21','14.el6_3','2013-11-27 23:53:25','Improved Linux Sound Server','PulseAudio is a sound server for Linux and other Unix like operating\nsystems. It is intended to be an improved drop-in replacement for the\nEnlightened Sound Daemon (ESOUND).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
