INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (272,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','check','x86_64','316759','0.9.8','1.1.el6','2013-11-27 23:51:08','A unit test framework for C','Check is a unit test framework for C. It features a simple interface for\ndefining unit tests, putting little in the way of the developer. Tests\nare run in a separate address space, so Check can catch both assertion\nfailures and code errors that cause segmentation faults or other signals.\nThe output from unit tests can be used within source code editors and IDEs.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
