INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (279,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','tar','x86_64','2616001','1.23','11.el6','2013-11-27 23:49:21','A GNU file archiving program','The GNU tar program saves many files together in one archive and can\nrestore individual files (or all of the files) from that archive. Tar\ncan also be used to add supplemental files to an archive and to update\nor list files in the archive. Tar includes multivolume support,\nautomatic archive compression/decompression, the ability to perform\nremote archives, and the ability to perform incremental and full\nbackups.\n\nIf you want to use tar for remote backups, you also need to install\nthe rmt package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
