INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (354,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pcre','x86_64','526268','7.8','6.el6','2013-11-27 23:49:15','Perl-compatible regular expression library','Perl-compatible regular expression library.\nPCRE has its own native API, but a set of \"wrapper\" functions that are based on\nthe POSIX API are also supplied in the library libpcreposix. Note that this\njust provides a POSIX calling interface to PCRE: the regular expressions\nthemselves still follow Perl syntax and semantics. The header file\nfor the POSIX-style functions is called pcreposix.h.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
