INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (369,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','mtools','x86_64','317815','4.0.12','1.el6','2013-11-27 23:51:09','Programs for accessing MS-DOS disks without mounting the disks','Mtools is a collection of utilities for accessing MS-DOS files.\nMtools allow you to read, write and move around MS-DOS filesystem\nfiles (normally on MS-DOS floppy disks).  Mtools supports Windows95\nstyle long file names, OS/2 XDF disks, and 2m disks\n\nMtools should be installed if you need to use MS-DOS disks\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (370,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','alsa-plugins-pulseaudio','x86_64','95730','1.0.21','3.el6','2013-11-27 23:53:29','Alsa to PulseAudio backend','This plugin allows any program that uses the ALSA API to access a PulseAudio\nsound daemon. In other words, native ALSA applications can play and record\nsound across a network. There are two plugins in the suite, one for PCM and\none for mixer control.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
