INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (372,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','texlive-texmf','noarch','14251343','2007','38.el6','2013-11-27 23:49:20','Architecture independent parts of the TeX formatting system','texlive-texmf is a texmf distribution based upon TeX Live. All of the files\ncontained in these packages are from the TeX Live zip files. The intent is to\nprovide a packaging similar in style and layout to teTeX.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (373,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','bridge-utils','x86_64','58535','1.2','10.el6','2013-11-27 23:56:32','Utilities for configuring the linux ethernet bridge','This package contains utilities for configuring the linux ethernet\nbridge. The linux ethernet bridge can be used for connecting multiple\nethernet devices together. The connecting is fully transparent: hosts\nconnected to one ethernet device see hosts connected to the other\nethernet devices directly.\n\nInstall bridge-utils if you want to use the linux ethernet bridge.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (374,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','python-setuptools','noarch','1532593','0.6.10','3.el6','2013-11-27 23:49:54','Easily build and distribute Python packages','Setuptools is a collection of enhancements to the Python distutils that allow\nyou to more easily build and distribute Python packages, especially ones that\nhave dependencies on other packages.\n\nThis package contains the runtime components of setuptools, necessary to\nexecute the software that requires pkg_resources.py.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (375,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libtool','x86_64','1998350','2.2.6','15.5.el6','2013-11-27 23:51:16','The GNU Portable Library Tool','GNU Libtool is a set of shell scripts which automatically configure UNIX and\nUNIX-like systems to generically build shared libraries. Libtool provides a\nconsistent, portable interface which simplifies the process of using shared\nlibraries.\n\nIf you are developing programs which will use shared libraries, but do not use\nthe rest of the GNU Autotools (such as GNU Autoconf and GNU Automake), you\nshould install the libtool package.\n\nThe libtool package also includes all files needed to integrate the GNU\nPortable Library Tool (libtool) and the GNU Libtool Dynamic Module Loader\n(ltdl) into a package built using the GNU Autotools (including GNU Autoconf\nand GNU Automake).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (376,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','sysstat','x86_64','826040','9.0.4','20.el6','2013-11-27 23:53:57','The sar and iostat system monitoring commands','This package provides the sar and iostat commands for Linux. Sar and\niostat enable system monitoring of disk, network, and other IO\nactivity.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
