INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (381,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libevent','x86_64','232536','1.4.13','4.el6','2013-11-27 23:51:26','Abstract asynchronous event notification library','The libevent API provides a mechanism to execute a callback function\nwhen a specific event occurs on a file descriptor or after a timeout\nhas been reached. libevent is meant to replace the asynchronous event\nloop found in event driven network servers. An application just needs\nto call event_dispatch() and can then add or remove events dynamically\nwithout having to change the event loop.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
