INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (384,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gdbm','x86_64','48586','1.8.0','36.el6','2013-11-27 23:49:22','A GNU set of database routines which use extensible hashing','Gdbm is a GNU database indexing library, including routines which use\nextensible hashing.  Gdbm works in a similar way to standard UNIX dbm\nroutines.  Gdbm is useful for developers who write C applications and\nneed access to a simple and efficient database or who are building C\napplications which will use such a database.\n\nIf you\'re a C developer and your programs need access to simple\ndatabase routines, you should install gdbm.  You\'ll also need to\ninstall gdbm-devel.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
