INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (391,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','glibc','x86_64','12955598','2.12','1.132.el6','2014-02-17 13:54:16','The GNU libc libraries','The glibc package contains standard libraries which are used by\nmultiple programs on the system. In order to save disk space and\nmemory, as well as to make upgrading easier, common system code is\nkept in one place and shared between programs. This particular package\ncontains the most important sets of shared libraries: the standard C\nlibrary and the standard math library. Without these two libraries, a\nLinux system will not function.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (392,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','nss-tools','x86_64','2886944','3.14.0.0','12.el6','2013-11-27 23:50:03','Tools for the Network Security Services','Network Security Services (NSS) is a set of libraries designed to\nsupport cross-platform development of security-enabled client and\nserver applications. Applications built with NSS can support SSL v2\nand v3, TLS, PKCS #5, PKCS #7, PKCS #11, PKCS #12, S/MIME, X.509\nv3 certificates, and other security standards.\n\nInstall the nss-tools package if you need command-line tools to\nmanipulate the NSS certificate and key database.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (393,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libbonobo-devel','x86_64','2303268','2.24.2','5.el6','2013-11-27 23:52:41','Libraries and headers for libbonobo','Bonobo is a component system based on CORBA, used by the GNOME desktop.\n\nThis package contains header files used to compile programs that\nuse Bonobo.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
