INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (397,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','junit','x86_64','776490','3.8.2','6.5.el6','2013-11-27 23:51:39','Java regression test package','JUnit is a regression testing framework written by Erich Gamma and Kent\nBeck. It is used by the developer who implements unit tests in Java.\nJUnit is Open Source Software, released under the IBM Public License and\nhosted on SourceForge.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (398,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','j2sdk','i586','66383259','1.4.2_19','fcs','2014-02-17 13:56:00','Java(TM) 2 Platform Standard Edition Runtime Environment Development Kit','The Java 2 SDK, Standard Edition includes the Java\nVirtual Machine, core class libraries and tools used\nby programmers to develop Java software applets and\napplications. The SDK also provides the foundation\nfor IDE (Integrated Development Environment) tools\nsuch as Sun\'s Forte for Java, Community Edition,\nthe Java(TM) 2 Platform, Enterprise Edition (J2EE),\nJava-based application servers and more.\nThe Java 2 Software Development Kit, SDK, is a\ndevelopment environment for building applications,\napplets, and components that can be deployed on\nthe Java platform. The Java 2 SDK software includes\ntools useful for developing and testing programs\nwritten in the Java programming language and running\non the Java platform.  These tools are designed to\nbe used from the command line.  Except for\nappletviewer, these tools do not provide a\ngraphical user interface.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (399,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Compress-Zlib','x86_64','52167','2.020','129.el6','2013-11-27 23:49:26','A module providing Perl interfaces to the zlib compression library','The Compress::Zlib module provides a Perl interface to the zlib\ncompression library. Most of the functionality provided by zlib is\navailable in Compress::Zlib.\n\nThe module can be split into two general areas of functionality,\nnamely in-memory compression/decompression and read/write access to\ngzip files.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (400,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gtk2-devel','x86_64','15003845','2.18.9','12.el6','2013-11-27 23:52:43','Development files for GTK+','This package contains the libraries amd header files that are needed\nfor writing applications with the GTK+ widget toolkit. If you plan\nto develop applications with GTK+, consider installing the gtk2-devel-docs\npackage.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
