INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (402,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','cifs-utils','x86_64','138662','4.8.1','18.el6','2013-11-27 23:54:22','Utilities for mounting and managing CIFS mounts','The SMB/CIFS protocol is a standard file sharing protocol widely deployed\non Microsoft Windows machines. This package contains tools for mounting\nshares on Linux using the SMB/CIFS protocol. The tools in this package\nwork in conjunction with support in the kernel to allow one to mount a\nSMB/CIFS share onto a client and use it as if it were a standard Linux\nfile system.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
