INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (405,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-ExtUtils-CBuilder','x86_64','60330','0.27','129.el6','2013-11-27 23:49:26','Compile and link C code for Perl modules','This module can build the C portions of Perl modules by invoking the\nappropriate compilers and linkers in a cross-platform manner. It was\nmotivated by the Module::Build project, but may be useful for other\npurposes as well.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
