INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (409,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','mailcap','noarch','52877','2.1.31','2.el6','2013-11-27 23:48:59','Helper application and MIME type associations for file types','The mailcap file is used by the metamail program.  Metamail reads the\nmailcap file to determine how it should display non-text or multimedia\nmaterial.  Basically, mailcap associates a particular type of file\nwith a particular program that a mail agent or other program can call\nin order to handle the file.  Mailcap should be installed to allow\ncertain programs to be able to handle non-text files.\n\nAlso included in this package is the mime.types file which contains a\nlist of MIME types and their filename \"extension\" associations, used\nby several applications e.g. to determine MIME types for filenames.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
