INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (443,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Module-Build','x86_64','471079','0.3500','129.el6','2013-11-27 23:50:33','Perl module for building and installing Perl modules','Module::Build is a system for building, testing, and installing Perl\nmodules. It is meant to be an alternative to ExtUtils::MakeMaker.\nDevelopers may alter the behavior of the module through subclassing in a\nmuch more straightforward way than with MakeMaker. It also does not\nrequire a make on your system - most of the Module::Build code is pure-perl and\nwritten in a very cross-platform way. In fact, you don\'t even need a\nshell, so even platforms like MacOS (traditional) can use it fairly easily. Its\nonly prerequisites are modules that are included with perl 5.6.0, and it\nworks fine on perl 5.005 if you can install a few additional modules.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (444,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gnome-screensaver','x86_64','6150333','2.28.3','24.el6','2013-11-27 23:54:44','GNOME Screensaver','gnome-screensaver is a screen saver and locker that aims to have\nsimple, sane, secure defaults and be well integrated with the desktop.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (445,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libjpeg-turbo','x86_64','476782','1.2.1','1.el6','2013-11-27 23:49:09','A MMX/SSE2 accelerated library for manipulating JPEG image files','The libjpeg-turbo package contains a library of functions for manipulating\nJPEG images. It also contains simple client programs for\naccessing the libjpeg functions. It contains cjpeg, djpeg, jpegtran,\nrdjpgcom and wrjpgcom. Cjpeg compresses an image file into JPEG format.\nDjpeg decompresses a JPEG file into a regular image file. Jpegtran\ncan perform various useful transformations on JPEG files. Rdjpgcom\ndisplays any text comments included in a JPEG file. Wrjpgcom inserts\ntext comments into a JPEG file.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
