INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (452,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','expat','x86_64','198018','2.0.1','11.el6_2','2013-11-27 23:49:10','An XML parser library','This is expat, the C library for parsing XML, written by James Clark. Expat\nis a stream oriented XML parser. This means that you register handlers with\nthe parser prior to starting the parse. These handlers are called when the\nparser discovers the associated structures in the document being parsed. A\nstart tag is an example of the kind of structures for which you may\nregister handlers.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (453,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','avalon-framework','x86_64','395547','4.1.4','7.el6','2013-11-27 23:51:49','Java components interfaces','The Avalon framework consists of interfaces that define relationships\nbetween commonly used application components, best-of-practice pattern\nenforcements, and several lightweight convenience implementations of the\ngeneric components.\nWhat that means is that we define the central interface Component. We\nalso define the relationship (contract) a component has with peers,\nancestors and children.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
