INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (463,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','yum-utils','noarch','308503','1.1.30','14.el6','2013-11-27 23:55:00','Utilities based around the yum package manager','yum-utils is a collection of utilities and examples for the yum package\nmanager. It includes utilities by different authors that make yum easier and\nmore powerful to use. These tools include: debuginfo-install,\nfind-repos-of-install, needs-restarting, package-cleanup, repoclosure,\nrepodiff, repo-graph, repomanage, repoquery, repo-rss, reposync,\nrepotrack, show-installed, show-changed-rco, verifytree, yumdownloader,\nyum-builddep, yum-complete-transaction, yum-config-manager, yum-debug-dump,\nyum-debug-restore and yum-groups-manager.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (464,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','readline','x86_64','433957','6.0','4.el6','2013-11-27 23:49:10','A library for editing typed command lines','The Readline library provides a set of functions that allow users to\nedit command lines. Both Emacs and vi editing modes are available. The\nReadline library includes additional functions for maintaining a list\nof previously-entered command lines for recalling or editing those\nlines, and for performing csh-like history expansion on previous\ncommands.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (465,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','jsch','noarch','213615','0.1.41','2.2.el6','2013-11-27 23:51:52','Pure Java implementation of SSH2','JSch allows you to connect to an sshd server and use port forwarding,\nX11 forwarding, file transfer, etc., and you can integrate its\nfunctionality into your own Java programs.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (466,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','cracklib-dicts','x86_64','9327207','2.8.16','4.el6','2013-11-27 23:49:32','The standard CrackLib dictionaries','The cracklib-dicts package includes the CrackLib dictionaries.\nCrackLib will need to use the dictionary appropriate to your system,\nwhich is normally put in /usr/share/dict/words. Cracklib-dicts also\ncontains the utilities necessary for the creation of new dictionaries.\n\nIf you are installing CrackLib, you should also install cracklib-dicts.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
