INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (470,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','glib2','x86_64','5190068','2.22.5','7.el6','2013-11-27 23:49:11','A library of handy utility functions','GLib is the low-level core library that forms the basis\nfor projects such as GTK+ and GNOME. It provides data structure\nhandling for C, portability wrappers, and interfaces for such runtime\nfunctionality as an event loop, threads, dynamic loading, and an\nobject system.\n\nThis package provides version 2 of GLib.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
