INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (472,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libsndfile','x86_64','770762','1.0.20','5.el6','2013-11-27 23:49:36','Library for reading and writing sound files','libsndfile is a C library for reading and writing sound files such as\nAIFF, AU, WAV, and others through one standard interface. It can\ncurrently read/write 8, 16, 24 and 32-bit PCM files as well as 32 and\n64-bit floating point WAV files and a number of compressed formats. It\ncompiles and runs on *nix, MacOS, and Win32.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
