INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (475,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','iotop','noarch','144635','0.3.2','3.el6','2013-11-27 23:55:06','Top like utility for I/O','Linux has always been able to show how much I/O was going on\n(the bi and bo columns of the vmstat 1 command).\niotop is a Python program with a top like UI used to\nshow of behalf of which process is the I/O going on.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (476,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','jakarta-commons-logging','noarch','89412','1.0.4','10.el6','2013-11-27 23:49:12','Jakarta Commons Logging Package','The commons-logging package provides a simple, component oriented\ninterface (org.apache.commons.logging.Log) together with wrappers for\nlogging systems. The user can choose at runtime which system they want\nto use. In addition, a small number of basic implementations are\nprovided to allow users to use the package standalone.\ncommons-logging was heavily influenced by Avalon\'s Logkit and Log4J. The\ncommons-logging abstraction is meant to minimize the differences between\nthe two, and to allow a developer to not tie himself to a particular\nlogging implementation.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (477,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','GConf2-gtk','x86_64','12784','2.28.0','6.el6','2013-11-27 23:52:04','Graphical GConf utilities','The GConf2-gtk package contains graphical GConf utilities\nwhich require GTK+.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (478,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','docbook-dtds','noarch','4571574','1.0','51.el6','2013-11-27 23:49:37','SGML and XML document type definitions for DocBook','The DocBook Document Type Definition (DTD) describes the syntax of\ntechnical documentation texts (articles, books and manual pages).\nThis syntax is XML-compliant and is developed by the OASIS consortium.\nThis package contains SGML and XML versions of the DocBook DTD.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (479,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libgnomeui-devel','x86_64','3170982','2.24.1','4.el6','2013-11-27 23:52:58','Libraries and headers for libgnome','\nGNOME (GNU Network Object Model Environment) is a user-friendly set of\nGUI applications and desktop tools to be used in conjunction with a\nwindow manager for the X Window System. The libgnomeui-devel package\nincludes the libraries and include files that you will need to\nuse libgnomeui.\n\nYou should install the libgnomeui-devel package if you would like to\ncompile GNOME applications. You do not need to install\nlibgnomeui-devel if you just want to use the GNOME desktop\nenvironment.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
