INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (581,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','xalan-j2','noarch','2031644','2.7.0','9.8.el6','2013-11-27 23:51:36','Java XSLT processor','Xalan is an XSLT processor for transforming XML documents into HTML,\ntext, or other XML document types. It implements the W3C Recommendations\nfor XSL Transformations (XSLT) and the XML Path Language (XPath). It can\nbe used from the command line, in an applet or a servlet, or as a module\nin other program.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (582,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','nspr','x86_64','273840','4.10.2','1.el6_5','2014-02-17 13:54:20','Netscape Portable Runtime','NSPR provides platform independence for non-GUI operating system\nfacilities. These facilities include threads, thread synchronization,\nnormal file and network I/O, interval timing and calendar time, basic\nmemory management (malloc and free) and shared library linking.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (583,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','autoconf','noarch','2605343','2.63','5.1.el6','2013-11-27 23:49:26','A GNU tool for automatically configuring source code','GNU Autoconf is a tool for configuring source code and Makefiles.\nUsing Autoconf, programmers can create portable and configurable\npackages, since the person building the package is allowed to\nspecify various configuration options.\n\nYou should install Autoconf if you are developing software and\nwould like to create shell scripts that configure your source code\npackages. If you are installing Autoconf, you will also need to\ninstall the GNU m4 package.\n\nNote that the Autoconf package is not required for the end-user who\nmay be configuring software with an Autoconf-generated script;\nAutoconf is only required for the generation of the scripts, not\ntheir use.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (584,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gtk-doc','noarch','552544','1.11','5.1.el6','2013-11-27 23:52:41','API documentation generation tool for GTK+ and GNOME','gtk-doc is a tool for generating API reference documentation.\nIt is used for generating the documentation for GTK+, GLib\nand GNOME.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (585,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','openldap','x86_64','792868','2.4.23','31.el6','2013-11-27 23:50:03','LDAP support libraries','OpenLDAP is an open source suite of LDAP (Lightweight Directory Access\nProtocol) applications and development tools. LDAP is a set of\nprotocols for accessing directory services (usually phone book style\ninformation, but other information is possible) over the Internet,\nsimilar to the way DNS (Domain Name System) information is propagated\nover the Internet. The openldap package contains configuration files,\nlibraries, and documentation for OpenLDAP.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (586,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Config-General','noarch','167913','2.52','1.el6','2014-05-14 13:36:35','Generic configuration module for Perl','This module opens a config file and parses its contents for\nyou. After parsing the module returns a hash structure which contains\nthe representation of the config file.\nThe format of config files supported by Config::General is inspired by\nthe well known Apache config format, in fact, this module is 100%\nread-compatible with Apache configs, but you can also just use simple\nname/value pairs in your config files.\nIn addition to the capabilities of an Apache config file it supports\nsome enhancements such as here-documents, C-style comments or\nmultiline options. It is also possible to save the config back to\ndisk, which makes the module a perfect backend for configuration\ninterfaces.\nIt is possible to use variables in config files and there exists also\nsupport for object oriented access to the configuration.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (587,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Encode-Detect','x86_64','189100','1.01','2.el6','2014-05-14 13:36:36','Encode::Encoding subclass that detects the encoding of data','This Perl module is an Encode::Encoding subclass that uses\nEncode::Detect::Detector to determine the charset of the input data and then\ndecodes it using the encoder of the detected charset.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
