INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (589,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Digest-SHA1','x86_64','105407','2.12','2.el6','2014-05-14 13:36:38','Digest-SHA1 Perl module','The Digest::SHA1 module allows you to use the NIST SHA-1 message\ndigest algorithm from within Perl programs. The algorithm takes as\ninput a message of arbitrary length and produces as output a 160-bit\n\"fingerprint\" or \"message digest\" of the input.\n\nThe Digest::SHA1 module provide a procedural interface for simple use,\nas well as an object oriented interface that can handle messages of\narbitrary length and which can read files directly.\n\nA binary digest will be 20 bytes long. A hex digest will be 40\ncharacters long. A base64 digest will be 27 characters long.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
