INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (592,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','cpp','x86_64','9970435','4.4.7','4.el6','2014-05-14 13:37:16','The C Preprocessor','Cpp is the GNU C-Compatible Compiler Preprocessor.\nCpp is a macro processor which is used automatically\nby the C compiler to transform your program before actual\ncompilation. It is called a macro processor because it allows\nyou to define macros, abbreviations for longer\nconstructs.\n\nThe C preprocessor provides four separate functionalities: the\ninclusion of header files (files of declarations that can be\nsubstituted into your program); macro expansion (you can define macros,\nand the C preprocessor will replace the macros with their definitions\nthroughout the program); conditional compilation (using special\npreprocessing directives, you can include or exclude parts of the\nprogram according to various conditions); and line control (if you use\na program to combine or rearrange source files into an intermediate\nfile which is then compiled, you can use line control to inform the\ncompiler about where each source line originated).\n\nYou should install this package if you are a C programmer and you use\nmacros.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
