INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (661,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','numpy','x86_64','10789483','1.4.1','9.el6','2013-11-27 23:50:41','A fast multidimensional array facility for Python','NumPy is a general-purpose array-processing package designed to\nefficiently manipulate large multi-dimensional arrays of arbitrary\nrecords without sacrificing too much speed for small multi-dimensional\narrays.  NumPy is built on the Numeric code base and adds features\nintroduced by numarray as well as an extended C-API and the ability to\ncreate arrays of arbitrary type.\n\nThere are also basic facilities for discrete fourier transform,\nbasic linear algebra and random number generation. Also included in\nthis package is a version of f2py that works properly with NumPy.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
