INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (678,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gstreamer-tools','x86_64','69024','0.10.29','1.el6','2013-11-27 23:50:43','common tools and files for GStreamer streaming media framework','GStreamer is a streaming media framework, based on graphs of filters which\noperate on media data. Applications using this library can do anything\nfrom real-time sound processing to playing videos, and just about anything\nelse media-related.  Its plugin-based architecture means that new data\ntypes or processing capabilities can be added simply by installing new\nplugins.\n\nThis package contains wrapper scripts for the command-line tools that work\nwith different major/minor versions of GStreamer.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
