INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (681,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libgnome','x86_64','2860946','2.28.0','11.el6','2013-11-27 23:52:06','GNOME base library','\nGNOME (GNU Network Object Model Environment) is a user-friendly set of\nGUI applications and desktop tools to be used in conjunction with a\nwindow manager for the X Window System. The libgnome package includes\nnon-GUI-related libraries that are needed to run GNOME. The libgnomeui\npackage contains X11-dependent GNOME library features.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (682,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','foomatic','x86_64','761581','4.0.4','1.el6_1.1','2013-11-27 23:53:05','Database of printers and printer drivers','Foomatic is a comprehensive, spooler-independent database of printers,\nprinter drivers, and driver descriptions. It contains utilities to\ngenerate driver description files and printer queues for CUPS, LPD,\nLPRng, and PDQ using the database. There is also the possibility to\nread the PJL options out of PJL-capable laser printers and take them\ninto account at the driver description file generation.\n\nThere are spooler-independent command line interfaces to manipulate\nqueues (foomatic-configure) and to print files/manipulate jobs\n(foomatic printjob).\n\nThe site http://www.linuxprinting.org/ is based on this database.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (683,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libsamplerate','x86_64','1640295','0.1.7','2.1.el6','2013-11-27 23:49:38','Sample rate conversion library for audio data','Secret Rabbit Code is a sample rate converter for audio. It is capable\nof arbitrary and time varying conversions. It can downsample by a\nfactor of 12 and upsample by the same factor. The ratio of input and\noutput sample rates can be a real number. The conversion ratio can\nalso vary with time for speeding up and slowing down effects.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (684,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','iw','x86_64','75619','0.9.17','4.el6','2013-11-27 23:50:50','A nl80211 based wireless configuration tool','iw is a new nl80211 based CLI configuration utility for wireless devices.\nCurrently you can only use this utility to configure devices which\nuse a mac80211 driver as these are the new drivers being written -\nonly because most new wireless devices being sold are now SoftMAC.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (685,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gmp','x86_64','657883','4.3.1','7.el6_2.2','2013-11-27 23:49:13','A GNU arbitrary precision library','The gmp package contains GNU MP, a library for arbitrary precision\narithmetic, signed integers operations, rational numbers and floating\npoint numbers. GNU MP is designed for speed, for both small and very\nlarge operands. GNU MP is fast because it uses fullwords as the basic\narithmetic type, it uses fast algorithms, it carefully optimizes\nassembly code for many CPUs\' most common inner loops, and it generally\nemphasizes speed over simplicity/elegance in its operations.\n\nInstall the gmp package if you need a fast arbitrary precision\nlibrary.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (686,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','tcsh','x86_64','1196177','6.17','24.el6','2013-11-27 23:55:11','An enhanced version of csh, the C shell','Tcsh is an enhanced but completely compatible version of csh, the C\nshell.  Tcsh is a command language interpreter which can be used both\nas an interactive login shell and as a shell script command processor.\nTcsh includes a command line editor, programmable word completion,\nspelling correction, a history mechanism, job control and a C language\nlike syntax.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
