INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (691,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libXau','x86_64','40009','1.0.6','4.el6','2013-11-27 23:49:13','Sample Authorization Protocol for X','This is a very simple mechanism for providing individual access to an X Window\nSystem display.It uses existing core protocol and library hooks for specifying\nauthorization data in the connection setup block to restrict use of the display\nto only those clients that show that they know a server-specific key\ncalled a \"magic cookie\".\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
