INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (701,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libedit','x86_64','185648','2.11','4.20080712cvs.1.el6','2013-11-27 23:49:45','The NetBSD Editline library','Libedit is an autotool- and libtoolized port of the NetBSD Editline library.\nIt provides generic line editing, history, and tokenization functions, similar\nto those found in GNU Readline.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
