INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (703,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libXcursor','x86_64','69445','1.1.13','2.el6','2013-11-27 23:49:14','Cursor management library','This is  a simple library designed to help locate and load cursors.\nCursors can be loaded from files or memory. A library of common cursors\nexists which map to the standard X cursor names.Cursors can exist in\nseveral sizes and the library automatically picks the best size.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (704,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','lsof','x86_64','911411','4.82','4.el6','2013-11-27 23:55:15','A utility which lists open files on a Linux/UNIX system','Lsof stands for LiSt Open Files, and it does just that: it lists\ninformation about files that are open by the processes running on a\nUNIX system.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (705,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','c2050','x86_64','30129','0.3b','3.1.el6','2013-11-27 23:52:09','Converts bitcmyk data to Lexmark 2050 printer language','This is a filter to convert bitcmyk data such as produced by ghostscript to\nthe printer language of Lexmark 2050 printers.  It is meant to be used\nby the PostScript Description files of the drivers from the foomatic package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (706,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','anthy','x86_64','25800356','9100h','10.1.el6','2013-11-27 23:49:46','Japanese character set input library','Anthy provides the library to input Japanese on the applications, such as\nX applications and emacs. and the user dictionaries and the users information\nwhich is used for the conversion, is stored into their own home directory.\nSo Anthy is secure than other conversion server.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (707,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','m17n-db-oriya','noarch','6082','1.5.5','1.1.el6','2013-11-27 23:50:53','m17n-db input maps for Oriya','This package contains m17n-db input maps for Oriya.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (708,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','binutils','x86_64','9814963','2.20.51.0.2','5.36.el6','2013-11-27 23:49:15','A GNU collection of binary utilities','Binutils is a collection of binary utilities, including ar (for\ncreating, modifying and extracting from archives), as (a family of GNU\nassemblers), gprof (for displaying call graph profile data), ld (the\nGNU linker), nm (for listing symbols from object files), objcopy (for\ncopying and translating object files), objdump (for displaying\ninformation from object files), ranlib (for generating an index for\nthe contents of an archive), readelf (for displaying detailed\ninformation about binary files), size (for listing the section sizes\nof an object or archive file), strings (for listing printable strings\nfrom files), strip (for discarding symbols), and addr2line (for\nconverting addresses to file and line).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (709,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','nano','x86_64','1588347','2.0.9','7.el6','2013-11-27 23:55:15','A small text editor','GNU nano is a small and friendly text editor.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (710,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libburn','x86_64','250512','0.7.0','2.el6','2013-11-27 23:52:09','Library for reading, mastering and writing optical discs','Libburn is an open-source library for reading, mastering and writing\noptical discs. For now this means only CD-R and CD-RW.\n\nThe project comprises of several more or less interdependent parts which\ntogether strive to be a usable foundation for application development.\nThese are libraries, language bindings, and middleware binaries which emulate\nclassical (and valuable) Linux tools.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (711,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','certmonger','x86_64','1296232','0.61','3.el6','2013-11-27 23:53:14','Certificate status monitor and PKI enrollment client','Certmonger is a service which is primarily concerned with getting your\nsystem enrolled with a certificate authority (CA) and keeping it enrolled.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (712,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','python','x86_64','21275023','2.6.6','36.el6','2013-11-27 23:49:49','An interpreted, interactive, object-oriented programming language','Python is an interpreted, interactive, object-oriented programming\nlanguage often compared to Tcl, Perl, Scheme or Java. Python includes\nmodules, classes, exceptions, very high level dynamic data types and\ndynamic typing. Python supports interfaces to many system calls and\nlibraries, as well as to various windowing systems (X11, Motif, Tk,\nMac and MFC).\n\nProgrammers can write new built-in modules for Python in C or C++.\nPython can be used as an extension language for applications that need\na programmable interface. This package contains most of the standard\nPython modules, as well as modules for interfacing to the Tix widget\nset for Tk and RPM.\n\nNote that documentation for Python is provided in the python-docs\npackage.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (713,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','m17n-db-tamil','noarch','20098','1.5.5','1.1.el6','2013-11-27 23:50:53','m17n-db input maps for Tamil','This package contains m17n-db input maps for Tamil.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (714,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','elfutils-libs','x86_64','488752','0.152','1.el6','2013-11-27 23:49:15','Libraries to handle compiled objects','The elfutils-libs package contains libraries which implement DWARF, ELF,\nand machine-specific ELF handling.  These libraries are used by the programs\nin the elfutils package.  The elfutils-devel package enables building\nother programs using these libraries.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (715,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','jomolhari-fonts','noarch','2293163','0.003','8.1.el6','2013-11-27 23:55:17','Jomolhari a Bhutanese style font for Tibetan and Dzongkha','Jomolhari is an TrueType OpenType Bhutanese style font for Dzongkha and\nTibetan text. It is based on Bhutanese manuscript examples, supports the\nUnicode and the Chinese encoding for Tibetan.\nThe font supports the standard combinations used in most texts.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (716,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libvisual','x86_64','414568','0.4.0','9.1.el6','2013-11-27 23:52:12','Abstraction library for audio visualisation plugins','Libvisual is an abstraction library that comes between applications and\naudio visualisation plugins.\n\nOften when it comes to audio visualisation plugins or programs that create\nvisuals they do depend on a player or something else, basically there is no\ngeneral framework that enable application developers to easy access cool\naudio visualisation plugins. Libvisual wants to change this by providing\nan interface towards plugins and applications, through this easy to use\ninterface applications can easily access plugins and since the drawing is\ndone by the application it also enables the developer to draw the visual\nanywhere he wants.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (717,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','fakeroot','x86_64','174537','1.12.2','22.2.el6','2013-11-27 23:53:17','Gives a fake root environment','fakeroot runs a command in an environment wherein it appears to have\nroot privileges for file manipulation. fakeroot works by replacing the\nfile manipulation library functions (chmod(2), stat(2) etc.) by ones\nthat simulate the effect the real library functions would have had,\nhad the user really been root.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (718,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','authconfig','x86_64','1939089','6.1.12','13.el6','2013-11-27 23:49:50','Command line tool for setting up authentication from network services','Authconfig is a command line utility which can configure a workstation\nto use shadow (more secure) passwords.  Authconfig can also configure a\nsystem to be a client for certain networked user information and\nauthentication schemes.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (719,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','elfutils-libelf-devel','x86_64','32139','0.152','1.el6','2013-11-27 23:50:53','Development support for libelf','The elfutils-libelf-devel package contains the libraries to create\napplications for handling compiled objects.  libelf allows you to\naccess the internals of the ELF object file format, so you can see the\ndifferent sections of an ELF file.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
