INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (721,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','thai-scalable-waree-fonts','noarch','342320','0.4.12','2.1.el6','2013-11-27 23:55:35','Thai Waree fonts','\nthaifonts-scalable provides a collection of free scalable Thai fonts.\n\nThis package provides the Waree family of Thai fonts.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (722,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','wavpack','x86_64','276798','4.60','1.1.el6','2013-11-27 23:52:15','A completely open audiocodec','WavPack is a completely open audio compression format providing lossless,\nhigh-quality lossy, and a unique hybrid compression mode. Although the\ntechnology is loosely based on previous versions of WavPack, the new\nversion 4 format has been designed from the ground up to offer unparalleled\nperformance and functionality.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (723,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','dhcp-common','x86_64','198000','4.1.1','34.P1.el6.centos','2013-11-27 23:53:19','Common files used by ISC dhcp client and server','DHCP (Dynamic Host Configuration Protocol) is a protocol which allows\nindividual devices on an IP network to get their own network\nconfiguration information (IP address, subnetmask, broadcast address,\netc.) from a DHCP server. The overall purpose of DHCP is to make it\neasier to administer a large network.\n\nThis package provides common files used by dhcp and dhclient package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (724,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','boost','x86_64','0','1.41.0','11.el6_1.2','2013-11-27 23:49:50','The free peer-reviewed portable C++ source libraries','Boost provides free peer-reviewed portable C++ source libraries.  The\nemphasis is on libraries which work well with the C++ Standard\nLibrary, in the hopes of establishing \"existing practice\" for\nextensions and providing reference implementations so that the Boost\nlibraries are suitable for eventual standardization. (Some of the\nlibraries have already been proposed for inclusion in the C++\nStandards Committee\'s upcoming C++ Standard Library Technical Report.)\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (725,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','abrt','x86_64','693466','2.0.8','15.el6.centos','2013-11-27 23:50:54','Automatic bug detection and reporting tool','abrt is a tool to help users to detect defects in applications and\nto create a bug report with all informations needed by maintainer to fix it.\nIt uses plugin system to extend its functionality.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (726,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','usbredir','x86_64','91017','0.5.1','1.el6','2013-11-27 23:49:16','USB network redirection protocol libraries','usbredir is a protocol for redirection USB traffic from a single USB device,\nto a different (virtual) machine then the one to which the USB device is\nattached. This package contains a number of libraries to help implementing\nsupport for usbredir:\n\nusbredirparser:\nA library containing the parser for the usbredir protocol\n\nusbredirhost:\nA library implementing the usb-host side of a usbredir connection.\nAll that an application wishing to implement an usb-host needs to do is:\n* Provide a libusb device handle for the device\n* Provide write and read callbacks for the actual transport of usbredir data\n* Monitor for usbredir and libusb read/write events and call their handlers\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (727,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','lohit-devanagari-fonts','noarch','113695','2.4.3','7.el6','2013-11-27 23:55:52','Free Devanagari Script Font','This package provides a free Devanagari Script TrueType/OpenType font.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (728,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libsemanage','x86_64','204095','2.0.43','4.2.el6','2013-11-27 23:52:15','SELinux binary policy manipulation library','Security-enhanced Linux is a feature of the Linux® kernel and a number\nof utilities with enhanced security functionality designed to add\nmandatory access controls to Linux.  The Security-enhanced Linux\nkernel contains new architectural components originally developed to\nimprove the security of the Flask operating system. These\narchitectural components provide general support for the enforcement\nof many kinds of mandatory access control policies, including those\nbased on the concepts of Type Enforcement®, Role-based Access\nControl, and Multi-level Security.\n\nlibsemanage provides an API for the manipulation of SELinux binary policies.\nIt is used by checkpolicy (the policy compiler) and similar tools, as well\nas by programs like load_policy that need to perform specific transformations\non binary policies such as customizing policy boolean settings.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (729,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','systemtap-devel','x86_64','4739538','1.8','7.el6','2013-11-27 23:53:25','Programmable system-wide instrumentation system - development headers, tools','This package contains the components needed to compile a systemtap\nscript from source form into executable (.ko) forms.  It may be\ninstalled on a self-contained developer workstation (along with the\nsystemtap-client and systemtap-runtime packages), or on a dedicated\nremote server (alongside the systemtap-server package).  It includes\na copy of the standard tapset library and the runtime library C files.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (730,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gamin-python','x86_64','89039','0.1.10','9.el6','2013-11-27 23:49:51','Python bindings for the gamin library','The gamin-python package contains a module that allow monitoring of\nfiles and directories from the Python language based on the support\nof the gamin package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (731,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','rpm-build','x86_64','322325','4.8.0','32.el6','2013-11-27 23:50:55','Scripts and executable programs used to build packages','The rpm-build package contains the scripts and executable programs\nthat are used to build packages using the RPM Package Manager.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (732,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','boost-system','x86_64','24226','1.41.0','11.el6_1.2','2013-11-27 23:49:17','Runtime component of boost system support library','\nRuntime component of Boost operating system support library, including\nthe diagnostics support that will be part of the C++0x standard\nlibrary.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (733,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','lohit-tamil-fonts','noarch','98270','2.4.5','5.el6','2013-11-27 23:56:09','Free Tamil font','This package provides a free Tamil truetype/opentype font.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (734,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','imsettings','x86_64','832407','0.108.0','3.6.el6','2013-11-27 23:52:16','Delivery framework for general Input Method configuration','IMSettings is a framework that delivers Input Method\nsettings and applies the changes so they take effect\nimmediately without any need to restart applications\nor the desktop.\n\nThis package contains the core DBus services and some utilities.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (735,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','fuse','x86_64','191803','2.8.3','4.el6','2013-11-27 23:53:28','File System in Userspace (FUSE) utilities','With FUSE it is possible to implement a fully functional filesystem in a\nuserspace program. This package contains the FUSE userspace tools to\nmount a FUSE filesystem.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (736,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','python-netaddr','noarch','5194930','0.7.5','4.el6','2013-11-27 23:49:52','Pythonic manipulation of IPv4, IPv6, CIDR, EUI and MAC network addresses','A pure Python network address representation and manipulation library.\n\nnetaddr provides a Pythonic way of working with :-\n\n- IPv4 and IPv6 addresses and subnets (including CIDR notation)\n- MAC (Media Access Control) addresses in multiple formats\n- IEEE EUI-64, OUI and IAB identifiers\n- a user friendly IP glob-style format\n\nIncluded are routines for :-\n\n- generating, sorting and summarizing IP addresses\n- converting IP addresses and ranges between various different formats\n- performing set based operations on groups of IP addresses and subnets\n- arbitrary IP address range calculations and conversions\n- querying IEEE OUI and IAB organisational information\n- querying of IP standards related data from key IANA data sources\n\nFor examples please visit the example wiki pages :-\n\n    http://code.google.com/p/netaddr/wiki/NetAddrExamples\n\nComplete API documentation for the latest release is available online :-\n\n    http://packages.python.org/netaddr/\n\nFor details on history changes and updates see the CHANGELOG :-\n\n    http://code.google.com/p/netaddr/wiki/CHANGELOG\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (737,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ed','x86_64','119832','1.1','3.3.el6','2013-11-27 23:51:08','The GNU line editor','Ed is a line-oriented text editor, used to create, display, and modify\ntext files (both interactively and via shell scripts).  For most\npurposes, ed has been replaced in normal usage by full-screen editors\n(emacs and vi, for example).\n\nEd was the original UNIX editor, and may be used by some programs.  In\ngeneral, however, you probably don\'t need to install it and you probably\nwon\'t use it.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (738,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','psmisc','x86_64','222302','22.6','15.el6_0.1','2013-11-27 23:49:17','Utilities for managing processes on your system','The psmisc package contains utilities for managing processes on your\nsystem: pstree, killall and fuser.  The pstree command displays a tree\nstructure of all of the running processes on your system.  The killall\ncommand sends a specified signal (SIGTERM if nothing is specified) to\nprocesses identified by name.  The fuser command identifies the PIDs\nof processes that are using specified files or filesystems.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (739,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','un-core-dinaru-fonts','noarch','3089871','1.0.2','0.15.080608.el6','2013-11-27 23:56:24','한글 은글꼴 Core 모음 - 은디나루','\n은글꼴 시리즈는 HLaTex개발자이신 은광희님이 1998년에 개발한 폰트입니다.\n2003년에 박원규님이 FontForge를 이용하여 트루타입폰트로 변환했습니다.\n은글꼴은 가장 일반적인 글꼴들입니다.\n\nCore 모음:\n- 은바탕: serif\n- 은디나루: fantasy\n- 은돋음: sans-serif\n- 은그래픽: sans-serif style\n- 은궁서: cursive, brush-stroke\n- 은필기: script\n\n이 패키지에는 은디나루글꼴이 포함되어 있습니다.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (740,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','netpbm','x86_64','2209091','10.47.05','11.el6','2013-11-27 23:52:17','A library for handling different graphics file formats','The netpbm package contains a library of functions which support\nprograms for handling various graphics file formats, including .pbm\n(portable bitmaps), .pgm (portable graymaps), .pnm (portable anymaps),\n.ppm (portable pixmaps) and others.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (741,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ipa-client','x86_64','303839','3.0.0','25.el6','2013-11-27 23:53:29','IPA authentication for use on clients','IPA is an integrated solution to provide centrally managed Identity (machine,\nuser, virtual machines, groups, authentication credentials), Policy\n(configuration settings, access control information) and Audit (events,\nlogs, analysis thereof). If your network uses IPA for authentication,\nthis package should be installed on every client machine.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (742,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','python-dateutil','noarch','350476','1.4.1','6.el6','2013-11-27 23:49:54','Powerful extensions to the standard datetime module','The dateutil module provides powerful extensions to the standard datetime\nmodule available in Python 2.3+.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (743,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','db4-cxx','x86_64','1645040','4.7.25','17.el6','2013-11-27 23:51:09','The Berkeley DB database library (version 4) for C++','The Berkeley Database (Berkeley DB) is a programmatic toolkit that\nprovides embedded database support for both traditional and\nclient/server applications. The Berkeley DB includes B+tree, Extended\nLinear Hashing, Fixed and Variable-length record access methods,\ntransactions, locking, logging, shared memory caching, and database\nrecovery. The Berkeley DB supports C, C++, Java, and Perl APIs. It is\nused by many applications, including Python and Perl, so this should\nbe installed on all systems.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (744,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libicu','x86_64','19496136','4.2.1','9.1.el6_2','2013-11-27 23:49:18','International Components for Unicode - libraries','The International Components for Unicode (ICU) libraries provide\nrobust and full-featured Unicode services on a wide variety of\nplatforms. ICU supports the most current version of the Unicode\nstandard, and they provide support for supplementary Unicode\ncharacters (needed for GB 18030 repertoire support).\nAs computing environments become more heterogeneous, software\nportability becomes more important. ICU lets you produce the same\nresults across all the various platforms you support, without\nsacrificing performance. It offers great flexibility to extend and\ncustomize the supplied services.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (745,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','rfkill','x86_64','10728','0.3','4.el6','2013-11-27 23:56:32','A tool for enabling and disabling wireless devices','rfkill is a simple tool for accessing the Linux rfkill device interface,\nwhich is used to enable and disable wireless networking devices, typically\nWLAN, Bluetooth and mobile broadband.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (746,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','eclipse-mylyn-java','x86_64','239068','3.4.2','9.el6','2013-11-27 23:53:30','Mylyn Bridge:  Java Development','Mylyn Task-Focused UI extensions for JDT.  Provides focusing of Java\nelement views and editors.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (747,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libproxy','x86_64','118431','0.3.0','3.el6_3','2013-11-27 23:49:54','A library handling all the details of proxy configuration','libproxy offers the following features:\n\n    * extremely small core footprint (< 35K)\n    * no external dependencies within libproxy core\n      (libproxy plugins may have dependencies)\n    * only 3 functions in the stable external API\n    * dynamic adjustment to changing network topology\n    * a standard way of dealing with proxy settings across all scenarios\n    * a sublime sense of joy and accomplishment\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (748,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libattr-devel','x86_64','56599','2.4.44','7.el6','2013-11-27 23:51:10','Extended attribute static libraries and headers','This package contains the libraries and header files needed to\ndevelop programs which make use of extended attributes.\nFor Linux programs, the documented system call API is the\nrecommended interface, but an SGI IRIX compatibility interface\nis also provided.\n\nCurrently only ext2, ext3 and XFS support extended attributes.\nThe SGI IRIX compatibility API built above the Linux system calls is\nused by programs such as xfsdump(8), xfsrestore(8) and xfs_fsr(8).\n\nYou should install libattr-devel if you want to develop programs\nwhich make use of extended attributes.  If you install libattr-devel,\nyou\'ll also want to install attr.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (749,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libXxf86misc','x86_64','25380','1.0.3','4.el6','2013-11-27 23:49:20','X.Org X11 libXxf86misc runtime library','X.Org X11 libXxf86misc runtime library\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
