INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (751,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','iputils','x86_64','299349','20071127','16.el6','2013-11-27 23:52:29','Network monitoring tools including ping','The iputils package contains basic utilities for monitoring a network,\nincluding ping. The ping command sends a series of ICMP protocol\nECHO_REQUEST packets to a specified network host to discover whether\nthe target machine is alive and receiving network traffic.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (752,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gnome-user-share','x86_64','1146008','2.28.2','3.el6','2013-11-27 23:53:56','Gnome user file sharing','\ngnome-user-share is a small package that binds together various free\nsoftware projects to bring easy to use user-level file sharing to the\nmasses.\n\nThe program is meant to run in the background when the user is logged\nin, and when file sharing is enabled a webdav server is started that\nshares the $HOME/Public folder. The share is then published to all\ncomputers on the local network using mDNS/rendezvous, so that it shows\nup in the Network location in GNOME.\n\nThe program also allows to share files using ObexFTP over Bluetooth.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (753,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','festival-lib','x86_64','1100862','1.96','18.el6','2013-11-27 23:49:55','Library for the Festival speech synthesis system','The shared library used by the Festival text-to-speech and speech synthesis\nsystem.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (754,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pnm2ppa','x86_64','887686','1.04','19.el6','2013-11-27 23:51:16','Drivers for printing to HP PPA printers','Pnm2ppa is a color driver for HP PPA host-based printers such as the\nHP710C, 712C, 720C, 722C, 820Cse, 820Cxi, 1000Cse, and 1000Cxi.\nPnm2ppa accepts Ghostscript output in PPM format and sends it to the\nprinter in PPA format.\n\nInstall pnm2ppa if you need to print to a PPA printer.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (755,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','flac','x86_64','722670','1.2.1','6.1.el6','2013-11-27 23:49:21','An encoder/decoder for the Free Lossless Audio Codec','FLAC stands for Free Lossless Audio Codec. Grossly oversimplified, FLAC\nis similar to Ogg Vorbis, but lossless. The FLAC project consists of\nthe stream format, reference encoders and decoders in library form,\nflac, a command-line program to encode and decode FLAC files, metaflac,\na command-line metadata editor for FLAC files and input plugins for\nvarious music players.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
