INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (758,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','qt-devel','x86_64','30301908','4.6.2','25.el6','2013-11-27 23:54:05','Development files for the Qt toolkit','This package contains the files necessary to develop\napplications using the Qt toolkit.  Includes:\nQt Linguist\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (759,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','opensp','x86_64','3065144','1.5.2','12.1.el6','2013-11-27 23:49:56','SGML and XML parser','OpenSP is an implementation of the ISO/IEC 8879:1986 standard SGML\n(Standard Generalized Markup Language). OpenSP is based on James\nClark\'s SP implementation of SGML. OpenSP is a command-line\napplication and a set of components, including a generic API.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
