INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (762,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','atmel-firmware','noarch','728154','1.3','7.el6','2013-11-27 23:56:36','Firmware for Atmel at76c50x wireless network chips','The drivers for Atmel at76c50x wireless network chips in the Linux 2.6.x kernel\nbut do not include the firmware.\nThis firmware needs to be loaded by the host on most cards using these chips.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (763,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','usermode-gtk','x86_64','208099','1.102','3.el6','2013-11-27 23:52:34','Graphical tools for certain user account management tasks','The usermode-gtk package contains several graphical tools for users:\nuserinfo, usermount and userpasswd.  Userinfo allows users to change\ntheir finger information.  Usermount lets users mount, unmount, and\nformat filesystems.  Userpasswd allows users to change their\npasswords.\n\nInstall the usermode-gtk package if you would like to provide users with\ngraphical tools for certain account management tasks.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
