INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (765,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pulseaudio-utils','x86_64','165170','0.9.21','14.el6_3','2013-11-27 23:49:57','PulseAudio sound server utilities','This package contains command line utilities for the PulseAudio sound server.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (766,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pango','x86_64','1039145','1.28.1','7.el6_3','2013-11-27 23:51:26','System for layout and rendering of internationalized text','Pango is a library for laying out and rendering of text, with an emphasis\non internationalization. Pango can be used anywhere that text layout is needed,\nthough most of the work on Pango so far has been done in the context of the\nGTK+ widget toolkit. Pango forms the core of text and font handling for GTK+.\n\nPango is designed to be modular; the core Pango layout engine can be used\nwith different font backends.\n\nThe integration of Pango with Cairo provides a complete solution with high\nquality text handling and graphics rendering.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (767,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','yajl','x86_64','91423','1.0.7','3.el6','2013-11-27 23:49:21','Yet Another JSON Library (YAJL)','Yet Another JSON Library. YAJL is a small event-driven\n(SAX-style) JSON parser written in ANSI C, and a small\nvalidating JSON generator.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (768,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ql23xx-firmware','noarch','262821','3.03.27','3.1.el6','2013-11-27 23:56:36','Firmware for qlogic 23xx devices','Firmware for qlogic 2300 and 2322 devices.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (769,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','openssh-clients','x86_64','1049931','5.3p1','84.1.el6','2013-11-27 23:52:36','An open source SSH client applications','OpenSSH is a free version of SSH (Secure SHell), a program for logging\ninto and executing commands on a remote machine. This package includes\nthe clients necessary to make encrypted connections to SSH servers.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (770,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','virt-viewer','x86_64','781609','0.5.2','18.el6','2013-11-27 23:54:11','Virtual Machine Viewer','Virtual Machine Viewer provides a graphical console client for connecting\nto virtual machines. It uses the GTK-VNC or SPICE-GTK widgets to provide\nthe display, and libvirt for looking up VNC/SPICE server details.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (771,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','spice-server','x86_64','1151756','0.12.0','12.el6','2013-11-27 23:49:58','Implements the server side of the SPICE protocol','The Simple Protocol for Independent Computing Environments (SPICE) is\na remote display system built for virtual environments which allows\nyou to view a computing \'desktop\' environment not only on the machine\nwhere it is running, but from anywhere on the Internet and from a wide\nvariety of machine architectures.\n\nThis package contains the runtime libraries for any application that wishes\nto be a SPICE server.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (772,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libsoup','x86_64','444157','2.28.2','4.el6','2013-11-27 23:51:30','Soup, an HTTP library implementation','Libsoup is an HTTP library implementation in C. It was originally part\nof a SOAP (Simple Object Access Protocol) implementation called Soup, but\nthe SOAP and non-SOAP parts have now been split into separate packages.\n\nlibsoup uses the Glib main loop and is designed to work well with GTK\napplications. This enables GNOME applications to access HTTP servers\non the network in a completely asynchronous fashion, very similar to\nthe Gtk+ programming model (a synchronous operation mode is also\nsupported for those who want it).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (773,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl','x86_64','35281079','5.10.1','129.el6','2013-11-27 23:49:24','Practical Extraction and Report Language','Perl is a high-level programming language with roots in C, sed, awk\nand shell scripting.  Perl is good at handling processes and files,\nand is especially good at handling text.  Perl\'s hallmarks are\npracticality and efficiency.  While it is used to do a lot of\ndifferent things, Perl\'s most common applications are system\nadministration utilities and web programming.  A large proportion of\nthe CGI scripts on the web are written in Perl.  You need the perl\npackage installed on your system so that your system can handle Perl\nscripts.\n\nInstall this package if you want to program in Perl or enable your\nsystem to handle Perl scripts.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (774,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','kbd','x86_64','991792','1.15','11.el6','2013-11-27 23:52:37','Tools for configuring the console (keyboard, virtual terminals, etc.)','The kbd package contains tools for managing a Linux\nsystem\'s console\'s behavior, including the keyboard, the screen\nfonts, the virtual terminals and font files.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (775,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','brasero-nautilus','x86_64','45972','2.28.3','6.el6','2013-11-27 23:54:13','Nautilus extension for brasero','The brasero-nautilus package contains the brasero nautilus extension.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (776,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','cvs','x86_64','1590551','1.11.23','15.el6','2013-11-27 23:50:02','A version control system','CVS (Concurrent Versions System) is a version control system that can\nrecord the history of your files (usually, but not always, source\ncode). CVS only stores the differences between versions, instead of\nevery version of every file you have ever created. CVS also keeps a log\nof who, when, and why changes occurred.\n\nCVS is very helpful for managing releases and controlling the\nconcurrent editing of source files among multiple authors. Instead of\nproviding version control for a collection of files in a single\ndirectory, CVS provides version control for a hierarchical collection\nof directories consisting of revision controlled files. These\ndirectories and files can then be combined together to form a software\nrelease.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (777,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','xml-commons-resolver','x86_64','425519','1.1','4.18.el6','2013-11-27 23:51:32','Resolver subproject of xml-commons','Resolver subproject of xml-commons.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (778,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Module-Load','x86_64','7492','0.16','129.el6','2013-11-27 23:49:25','Runtime require of both modules and files','Module::Load eliminates the need to know whether you are trying to\nrequire either a file or a module.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (779,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','glibc-common','x86_64','112399708','2.12','1.132.el6','2014-02-17 13:54:13','Common binaries and locale data for glibc','The glibc-common package includes common binaries for the GNU libc\nlibraries, as well as national language (locale) support.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
