INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (816,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','khmeros-fonts-common','noarch','0','5.0','9.el6','2013-11-27 23:49:01','Common files of khmeros-fonts','\nThe Khmer OS fonts include Khmer and Latin alphabets, and they have equivalent\nsizes for Khmer and English alphabets, so that when texts mix both it is not\nnecessary to have different point sizes for the text in each language.\n\nThey were created by Danh Hong of the Cambodian Open Institute.\n\nThis package consists of files used by other khmeros-fonts packages.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
