INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (857,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','elfutils-libelf','x86_64','920293','0.152','1.el6','2013-11-27 23:49:10','Library to read and write ELF files','The elfutils-libelf package provides a DSO which allows reading and\nwriting ELF files on a high level.  Third party programs depend on\nthis package to read internals of ELF files.  The programs of the\nelfutils package use it also to generate new ELF files.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
