INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (859,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gzip','x86_64','225845','1.3.12','18.el6','2013-11-27 23:49:31','The GNU data compression program','The gzip package contains the popular GNU gzip data compression\nprogram. Gzipped files have a .gz extension.\n\nGzip should be installed on your system, because it is a\nvery commonly used data compression program.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
