INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (862,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-XML-Twig','noarch','619078','3.34','1.el6','2013-11-27 23:55:00','A perl module for processing huge XML documents in tree mode','This module provides a way to process XML documents. It is build on\ntop of XML::Parser.  XML::Twig offers a tree interface to the\ndocument, while allowing you to output the parts of it that have been\ncompletely processed.  It allows minimal resource (CPU and memory)\nusage by building the tree only for the parts of the documents that\nneed actual processing, through the use of the twig_roots and\ntwig_print_outside_roots options.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (863,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libsepol','x86_64','248680','2.0.41','4.el6','2013-11-27 23:49:11','SELinux binary policy manipulation library','Security-enhanced Linux is a feature of the Linux® kernel and a number\nof utilities with enhanced security functionality designed to add\nmandatory access controls to Linux.  The Security-enhanced Linux\nkernel contains new architectural components originally developed to\nimprove the security of the Flask operating system. These\narchitectural components provide general support for the enforcement\nof many kinds of mandatory access control policies, including those\nbased on the concepts of Type Enforcement®, Role-based Access\nControl, and Multi-level Security.\n\nlibsepol provides an API for the manipulation of SELinux binary policies.\nIt is used by checkpolicy (the policy compiler) and similar tools, as well\nas by programs like load_policy that need to perform specific transformations\non binary policies such as customizing policy boolean settings.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
