INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (865,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','jpackage-utils','noarch','168678','1.7.5','3.12.el6','2013-11-27 23:49:36','JPackage utilities','Utilities for the JPackage Project <http://www.jpackage.org/>.\n\nIt contains also the License, man pages, documentation, XSL files of general\nuse with maven2, a header file for spec files, etc. Please See\nthe /usr/share/doc/jpackage-utils-1.7.5/jpackage-utils-README file for more\ninformation.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
